package com.syntax.class02;

public class ArthemeticOperatorExcersise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		System.out.println("---------1Exp----------------------");
		int a=10;
		int b=5;
		System.out.println(a+b);
		System.out.println(a/b);
		System.out.println(a*b);
		System.out.println(a-b);
		System.out.println(a%b);
		
		
		System.out.println("---------------2Exp--------------------");
		int x=10;
		int y=20;
		int sum,sub,mult,div;
		
		sum=x+y;
		System.out.println(sum);  // 30
		sub=x-y;
		System.out.println(sub);  // -10
		mult=x*y;
		System.out.println(mult);  // 200
		div=x/y;
		System.out.println(div);  // 0
		
		
		System.out.println("---------------------3Exp---------------------");
		double num1=3.99;
		double num2=4.99;
		
		double sum1,sub1,mult1,div1;
		sum1=num1+num2;
		System.out.println(sum1);
		sub1=num1*num2;
		System.out.println(sub1);
		mult1= num1-num2;
		System.out.println(mult1);
		div1= num1/num2;
		System.out.println(div1);
		
		
		System.out.println("---------------------4Exp-------------------------");
		int t=10;
		int r=3;
		int result;
		result = t/r;
		System.out.println(result);
		result=t+r;
		System.out.println(result);
		result=t-r;
		System.out.println(result);
		result=t*r;
		System.out.println(result);
		result=t%r;
		System.out.println(result);
		
		
		System.out.println("----------------------5Exp------------------------------");
		double e=10.0;
		double f=3.0;
		
		double result1;
		result1= e/f;
		System.out.println(result1);
		
		
		System.out.println("---------------------------6Exp-----------------------");
		
		float c=10.0f;
		float d=3.0f;
		float result2;
		result2= c/d;
		System.out.println(result2);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
